import React from 'react';
import './CycleTable.css';
import Loader from '../reusableComponents/Loader';
import { Progress } from 'reactstrap';

function totalValues(data,key) {
    let totalVal=0;
    for (var i=0;i<=data.length;i++) {
        for (var k in data[i]) {
            if (k == key) {
                totalVal += data[i][k];
            }
        }
    }
    return totalVal;
}

const CycleTable=(props)=>{


    let view=null;
    let rows= props.data.map((row)=>{return(
        <tr className="tableContent" key={row.ID}>
            <th>{row.ID}</th>
            <th className="wrapLongWord">{row.Description}</th>
            <th>
                <div className="progress">
                    <div className="progress-bar progress-bar-success" role="progressbar" style="width:40%">
                        Free Space
                    </div>
                    <div className="progress-bar progress-bar-warning" role="progressbar" style="width:10%">
                        Warning
                    </div>
                    <div className="progress-bar progress-bar-danger" role="progressbar" style="width:20%">
                        Danger
                    </div>
                </div>
            </th>

        </tr>)
    });

    if (props.data.length>0)
    {

        view =
            <table >
            <thead >
            <tr >
                <th>Cycle ID</th>
                <th>Cycle Name</th>
                <th>Ex Running</th>

            </tr>
            </thead>
            <tbody >
            {rows}
            </tbody>
            <tfoot>
            <tr >
                <th></th>
                <th></th>
                <th>{totalValues(props.data,'Ex_Running')}</th>
            </tr>
            </tfoot>
        </table>

    }
    else{
        view=<Loader/>
    }
    return(
        <div>
            {view!=null ?(
                <div className="ui fixed table">
                    {view}
                </div>
            ):(
                <div>
                    <Loader/>
                </div>
            )
            }
        </div>
    )
}

export default CycleTable;
