import React from 'react';
import './FilterBar.css';
import { Accordion, Icon,Form, Input, TextArea, Button, Select,Dropdown } from 'semantic-ui-react';

const genderOptions = [
    { key: 'm', text: 'Male', value: 'male' },
    { key: 'f', text: 'Female', value: 'female' },
    { key: 'o', text: 'Other', value: 'other' },
];
const optionsD=[{key:'Swift_Prod_33',text:'Swift_Prod_33',value:'Swift_Prod_33'}]

class FilterBar extends React.Component{
    //TODO: authentication for all 3+ correction to oven input
    state={
    activeIndex:-1,
    chosenData:{
        DB:0,
        Oven:0,
        cycle:0,
        }
};


    handleClick = (e, titleProps) => {
        const { index } = titleProps;
        const { activeIndex } = this.state;
        const newIndex = activeIndex === index ? -1 : index;

        this.setState({ activeIndex: newIndex })
    };
    //TODO :change db and oven to dropdown menus
    render(){
        let optionsO=[];
        let cnt=0;
        if(this.props.data.length>0) {
            for (var i in this.props.data) {
                optionsO[cnt] = {
                    key: this.props.data[i].oven,
                    text: this.props.data[i].oven,
                    value: this.props.data[i].oven
                }
                cnt++;
            }
        }
        const { activeIndex } = this.state;
        return(
            <div className="filterBar">
                <Accordion syled>
                    <Accordion.Title active={activeIndex === 0} index={0} onClick={this.handleClick}>
                        <Icon name='dropdown' />
                        Filter
                    </Accordion.Title>
                    <Accordion.Content active={activeIndex === 0}>
                        <Input type="text" placeholder="choose cycle"
                               value={this.state.chosenData.cycle==0 ? "" : this.state.chosenData.cycle} onChange={e=>this.setState(Object.assign(this.state.chosenData,{cycle:e.target.value}))}/>
                        <Input type="text" placeholder="choose DB"
                               value={this.state.chosenData.DB==0 ? "" : this.state.chosenData.DB} onChange={e=>this.setState(Object.assign(this.state.chosenData,{DB:e.target.value}))}/>
                        <Dropdown placeholder="Choose Oven" selection options={optionsO} value={this.state.Oven} onChange={(e, { value }) => this.setState(Object.assign(this.state.chosenData,{Oven:value}))}/>
                        <Dropdown placeholder="Choose DB" selection options={optionsD} value={this.state.DB} onChange={(e, { value }) => this.setState(Object.assign(this.state.chosenData,{DB:value}))}/>
                        <div>
                        <Button onClick={()=>this.props.toDB(this.state.chosenData)} >set</Button>
                        </div>
                    </Accordion.Content>
                    <Accordion.Title active={activeIndex === 1} index={1} onClick={this.handleClick}>
                        <Icon name="dropdown"/>
                        Form exmple
                    </Accordion.Title>
                    <Accordion.Content active={activeIndex==1}>
                        <Form >
                            <Form.Group >
                                <Form.Field
                                    id='form-input-control-first-name'
                                    control={Input}
                                    label='Cycles'
                                    placeholder='Enter cycle id'
                                />
                            </Form.Group>
                            <Form.Group>
                                <Form.Field
                                    control={Select}
                                    options={genderOptions}
                                    label={{ children: 'Gender', htmlFor: 'form-select-control-gender' }}
                                    placeholder='Database'
                                    search
                                    searchInput={{ id: 'form-select-control-gender' }}
                                />
                            </Form.Group>
                            <Form.Group>
                                <Form.Field
                                    control={Select}
                                    options={genderOptions}
                                    label={{ children: 'Oven', htmlFor: 'form-select-control-gender' }}
                                    placeholder='Oven'

                                    search
                                    searchInput={{ id: 'form-select-control-gender' }}
                                />
                            </Form.Group>
                            <Form.Field
                                id='form-button-control-public'
                                control={Button}
                                content='Set'
                            />
                        </Form>
                    </Accordion.Content>
                </Accordion>
            </div>
        )
    }
}

export default FilterBar;
