import React from 'react';
import './CycleTable.css';
import Loader from '../reusableComponents/Loader';
import { Progress } from 'reactstrap';

function totalValues(data,key) {
    let totalVal=0;
    for (var i=0;i<=data.length;i++) {
        for (var k in data[i]) {
            if (k == key) {
                totalVal += data[i][k];
            }
        }
    }
    return totalVal;
}

const CycleTable=(props)=>{


    let view=null;
    let rows= props.data.map((row)=>{return(
        <tr className="tableContent" key={row.ID}>
            <th>{row.ID}</th>
            <th className="wrapLongWord">{row.Description}</th>
            <th>
                <Progress multi style={{width:'300px'}}>
                    <Progress bar striped value="45">running</Progress>
                    <Progress bar animated color="success" value="30">Animated Stripes</Progress>
                    <Progress bar color="info" value="25">Plain</Progress>
                </Progress>
            </th>

        </tr>)
    });

    if (props.data.length>0)
    {

        view =
            <table >
            <thead >
            <tr >
                <th>Cycle ID</th>
                <th>Cycle Name</th>
                <th>Ex Running</th>

            </tr>
            </thead>
            <tbody >
            {rows}
            </tbody>
            <tfoot>
            <tr >
                <th></th>
                <th></th>
                <th>{totalValues(props.data,'Ex_Running')}</th>
            </tr>
            </tfoot>
        </table>

    }
    else{
        view=<Loader/>
    }
    return(
        <div>
            {view!=null ?(
                <div className="ui fixed table">
                    {view}
                </div>
            ):(
                <div>
                    <Loader/>
                </div>
            )
            }
        </div>
    )
}

export default CycleTable;
