import React from 'react';
import './DashboardBody.css';
import Progress from './DashboradBody/Progress';
import CycleGraph from './DashboradBody/CycleGraph';
import CycleTable from './DashboradBody/CycleTable';
import OvensView from './DashboradBody/OvensView.js';
import axios from 'axios';
import Title from './Title';
import SideNav, { Toggle, Nav, NavItem, NavIcon, NavText } from '@trendmicro/react-sidenav';
import '@trendmicro/react-sidenav/dist/react-sidenav.css';


class DashboardBody extends React.Component{
    constructor(props) {
        super(props);

        this.state = {
            data: [],
            id: 0,
            message: null,
            intervalIsSet: false,
            objectToUpdate: null,
            loading: true,
            table_data: [],
            executions_data:[],
            chamber_data:[],
            setups_status:[],
        };

        this.getDataFromDb = this.getDataFromDb.bind(this);
    }


    componentDidMount() {
        this.getDataFromDb();
        if (!this.state.intervalIsSet) {
            let interval = setInterval(this.getDataFromDb, 12000000);//timeout currently one minute
            this.setState({ intervalIsSet: interval });
        }
    }

    componentWillUnmount() {
        if (this.state.intervalIsSet) {
            clearInterval(this.state.intervalIsSet);
            this.setState({ intervalIsSet: null });
        }
    }


    getDataFromDb = ()=> {
        //TODO: my data is an array, so i need to iterate over the array using Array.prototype.map()- to work the right way.

        axios.get('http://localhost:3002/api/cycles', {})
            .then((res) => {
                this.setState({table_data: res.data});
                console.log(this.state.table_data);
            })
            .catch(function (error) {
                console.log(error);
            });
        axios.get('http://localhost:3002/api/executions', {})
            .then((res) => {
                this.setState({executions_data: res.data});
                console.log(this.state.executions_data);
            })
            .catch(function (error) {
                console.log(error);
            });
        axios.get('http://localhost:3002/api/chamber',{})
            .then((res)=>{
                this.setState({chamber_data:res.data});
                console.log(this.state.chamber_data);
        })
            .catch(function (error) {
                console.log(error);
            });
            axios.get('http://localhost:3002/api/ovenUtil',{})
                .then((res)=>{
                    this.setState({setups_status:res.data});//here im returning res instead of res.data
                    console.log(this.state.setups_status);
                })
                .catch(function (error) {
                    console.log(error);
                });
    }

    updateDB = (idToUpdate, updateToApply) => {
        axios.post("http://localhost:3002/api/updateData", {
            id: idToUpdate,
            update: { message: updateToApply }
        }).then(function (response) {

            console.log(response);
        })
            .catch(function (error) {
                console.log(error);
                window.alert(error);
            });
    };

    ConvObjToArr_ovenData(){
        var mappedOvenData=new Map();
        if (this.state.chamber_data.length>0 && this.state.setups_status.length>0){
            const chamber=this.state.chamber_data[0];
            const setups=this.state.setups_status;
            for(var k in chamber )
            {
                mappedOvenData.set(k,chamber[k])
            }
            let arr=[];
            let cnt=0;
            for(let i=0;i<setups.length;i++)
            {

                for(var z in setups[i] ){
                    arr[cnt]=setups[i][z];
                    if(cnt===1){
                        mappedOvenData.set(arr[1],arr[0]);
                        cnt=0;
                    }
                    else{cnt++};
                }
            }
        }
        return mappedOvenData;
    }

    render() {

        return (
            <div className="grid-container">
                <div className="header">
                    <div className="">
                        <h1 ><Title/></h1>
                    </div>
                </div>
                <div className="sidenav">
                    <SideNav
                        onSelect={(selected) => {
                            // Add your code here
                        }}
                    >
                        <SideNav.Toggle />
                        <SideNav.Nav defaultSelected="home">
                            <NavItem eventKey="home">
                                <NavIcon>
                                    <i className="fa fa-fw fa-home" style={{ fontSize: '1.75em' }} />
                                </NavIcon>
                                <NavText>
                                    Home
                                </NavText>
                            </NavItem>
                            <NavItem eventKey="charts">
                                <NavIcon>
                                    <i className="fa fa-fw fa-line-chart" style={{ fontSize: '1.75em' }} />
                                </NavIcon>
                                <NavText>
                                    Charts
                                </NavText>
                                <NavItem eventKey="charts/linechart">
                                    <NavText>
                                        Line Chart
                                    </NavText>
                                </NavItem>
                                <NavItem eventKey="charts/barchart">
                                    <NavText>
                                        Bar Chart
                                    </NavText>
                                </NavItem>
                            </NavItem>
                        </SideNav.Nav>
                    </SideNav>
                </div>
                <div className="dash">
                        <div className="status">
                            <Progress data={this.state.executions_data}/>
                        </div>
                        <div className="table">
                            <CycleTable data={this.state.table_data}/>
                            {/*{postView}*/}
                        </div>
                        <div className="stats">
                            <h2 style={{marginLeft:5}}>Analytics: </h2>
                            <ul>
                                <li> PEC - EOL :FLX 74890</li>
                                <li> FLX64332 has failed last 4 tests</li>
                                <li> DMElinkStartUp fail occured 12 times </li>
                                <li> 12 Jiras have benn assigned.. </li>
                            </ul>
                        </div>
                        <div className="chart">
                            <CycleGraph data={this.state.table_data}/>
                        </div>
                        <div className="ovens">
                            <OvensView data={this.ConvObjToArr_ovenData()} />
                        </div>
                </div>
            </div>
        )
    }
}

let postView= <div>
    <input
        type="text"
        style={{ width: "100px" }}
        onChange={e => this.setState({ idToUpdate: e.target.value })}
        placeholder="id"
    />
    <input
        type="text"
        style={{ width: "100px" }}
        onChange={e => this.setState({ updateToApply: e.target.value })}
        placeholder="value"
    />
    <button
        onClick={() =>
            this.updateDB(this.state.idToUpdate, this.state.updateToApply)
        }
    >
        Send Data To Backend
    </button>
</div>

export default DashboardBody;
