import React from 'react';
import img1 from '../assets/Cake.JPG';
import d3 from 'react-d3-graph';


const CycleGraph=()=>{
    return(
        <div>
            <div className="body">
                <div id="app"></div>
            </div>
        </div>
    )
};

function translate(x, y) {
    return `translate(${x}, ${y})`;
}

class Slice extends React.Component {
    render() {
        let {value, label, fill, innerRadius = 0, outerRadius, cornerRadius, padAngle} = this.props;
        let arc = d3.svg.arc()
            .innerRadius(innerRadius)
            .outerRadius(outerRadius)
            .cornerRadius(cornerRadius)
            .padAngle(padAngle);
        return (
            <g>
                <path d={arc(value)} fill={fill} />
                <text transform={translate(...arc.centroid(value))}
                      dy=".35em"
                      className="label">
                    {label}
                </text>
            </g>
        );
    }
}

class Pie extends React.Component {
    constructor(props) {
        super(props);
        this.colorScale = d3.scale.category10();
        this.renderSlice = this.renderSlice.bind(this);
    }

    render() {
        let {x, y, data} = this.props;
        let pie = d3.layout.pie();
        return (
            <g transform={translate(x, y)}>
                {pie(data).map(this.renderSlice)}
            </g>
        );
    }

    renderSlice(value, i) {
        let {innerRadius, outerRadius, cornerRadius, padAngle} = this.props;
        return (
            <Slice key={i}
                   innerRadius={innerRadius}
                   outerRadius={outerRadius}
                   cornerRadius={cornerRadius}
                   padAngle={padAngle}
                   value={value}
                   label={value.data}
                   fill={this.colorScale(i)} />
        );
    }
}

class App extends React.Component {
    render() {
        let width = window.innerWidth;
        let height = window.innerHeight;
        let minViewportSize = Math.min(width, height);
        let radius = (minViewportSize * .9) / 2;
        let x = width / 2;
        let y = height / 2;

        return (
            <svg width="100%" height="100%">
                <Pie x={x}
                     y={y}
                     innerRadius={radius * .35}
                     outerRadius={radius}
                     cornerRadius={7}
                     padAngle={.02}
                     data={this.props.data} />
            </svg>
        );
    }
}

ReactDOM.render(
    <App data={[5, 2, 7, 1, 1]} />,
    document.getElementById('app')
);

export default CycleGraph;
