import React from 'react';
import { Accordion, Icon } from 'semantic-ui-react';

class FilterBar extends React.Component{
    //TODO: authentication for all 3+ correction to oven input
    state={
    activeIndex:0,
    chosenData:{
        DB:0,
        Oven:0,
        cycle:0,
        }
};
    handleClick = (e, titleProps) => {
        const { index } = titleProps;
        const { activeIndex } = this.state;
        const newIndex = activeIndex === index ? -1 : index;

        this.setState({ activeIndex: newIndex })
    };

    render(){
        const { activeIndex } = this.state;
        return(
            <div>
                <Accordion>
                    <Accordion.Title active={activeIndex === 0} index={0} onClick={this.handleClick}>
                        <Icon name='dropdown' />
                        Filter
                    </Accordion.Title>
                    <Accordion.Content active={activeIndex === 0}>
                        <input type="text" placeholder="choose cycle"
                               value={this.state.cycle==0 ? "" : this.state.cycle} onChange={e=>this.setState(prevState=>({
                                                                                                chosenData: {
                                                                                                    ...prevState.chosenData,
                                                                                                    cycle:e.target.value
                                                                                                }
                        }))}/>
                        <input type="text" placeholder="choose DB"
                               value={this.state.DB==0 ? "" : this.state.DB} onChange={e=>this.setState(prevState=>({
                                                                                                chosenData: {
                                                                                                    ...prevState.chosenData,
                                                                                                    DB:e.target.value
                            }
                        }))}/>
                        <input type="text" placeholder="choose Oven"
                               value={this.state.Oven==0 ? "" : this.state.Oven} onChange={e=>this.setState(prevState=>({
                                                                                            chosenData: {
                                                                                                ...prevState.chosenData,
                                                                                                Oven:e.target.value
                                                                                            }
                        }))}/>
                        <button onClick={()=>this.props.toDB(this.state.chosenData)} >set</button>
                    </Accordion.Content>
                </Accordion>
            </div>
        )
    }
}

export default FilterBar;
